{ ... ... /* activate the tasks */ (void)ActivateTask(App_Task); (void)ActivateTask(Task10ms); (void)ActivateTask(Task1ms); (void)ActivateTask(Task20ms); ... ... //设置定时alarm，1ms, 2ms, 10ms ... ... (void)SetRelAlarm(Rte_Al_TE_Task1ms_0_1ms, RTE_MSEC_SystemTimer(0) + (TickType)1, RTE_MSEC_SystemTimer(1)); (void)SetRelAlarm(Rte_Al_TE_Task2ms_0_2ms, RTE_MSEC_SystemTimer(0) + (TickType)1, RTE_MSEC_SystemTimer(2)); (void)SetRelAlarm(Rte_Al_TE_App_AppRunnable, RTE_MSEC_SystemTimer(0) + (TickType)1, RTE_MSEC_SystemTimer(10)); ... ...
} //RTE controlled tasks
//App_Task
TASK(App_Task)
{ EventMaskType ev; for(;;) { (void)WaitEvent(Rte_Ev_Run_DemoApp_DemoRunnable); (void)GetEvent(App_Task, &ev); (void)ClearEvent(ev & (Rte_Ev_Run_DemoApp_DemoRunnable)); if ((ev & Rte_Ev_Run_DemoApp_DemoRunnable) != (EventMaskType)0) { /* call runnable */ AppRunnable(); } }
} TASK(Task10ms) { EventMaskType ev; for(;;) { (void)WaitEvent(Rte_Ev_Cyclic_Task10ms_0_10ms); (void)GetEvent(Task10ms, &ev); (void)ClearEvent(ev & (Rte_Ev_Cyclic_Task10ms_0_10ms)); if ((ev & Rte_Ev_Cyclic_Task10ms_0_10ms) != (EventMaskType)0) { //10ms swc runnable AppSiganlRunnable(); AppConfigRunnable(); AppInputRunnable(); ... ... } }

